// hide #back-top first
$("#back-top").hide();

// fade in #back-top

$(window).scroll(function () {
	if ($(this).scrollTop() > 100) {
		$('#back-top').fadeIn();
	} else {
		$('#back-top').fadeOut();
	}
});

// scroll body to 0px on click
$('#back-top a').on("click", function(){
	$('body,html').animate({
		scrollTop: 0
	}, 800);
	return false;
});

// Closes the Responsive Menu on Menu Item Click
$('.navbar-collapse ul li a').click(function() {
	$('.navbar-toggle:visible').click();
});

// slider
$('.bxslider').bxSlider({
	pager: false,
	speed: 1000,
    slideMargin: 0,
	prevText: '<i class="fa fa-angle-left"></i>',
	nextText: '<i class="fa fa-angle-right"></i>',
	easing: 'ease-in-out'
});
$(document).ready(function(){
  $("#showmore").on("hide.bs.collapse", function(){
    $("#showmorebtn").html('Show more');
  });
  $("#showmore").on("show.bs.collapse", function(){
    $("#showmorebtn").html('Show less');
  });
});
$(document).ready(function(){
  $("#addcomment").on("hide.bs.collapse", function(){
    $("#addcommentbtn").html('Add new Comment');
  });
  $("#addcomment").on("show.bs.collapse", function(){
    $("#addcommentbtn").html('Close');
  });
});
